    type: CommandType.Modal,
    plugins: [
        assertFields({
            fields: {
            // check the modal field "mcUsernameInput" with the regex /a+b+c/
                mcUsernameInput: /a+b+c+/
            },
            failure: (errors, interaction) => {
                interaction.reply(errors.join("\n"))
            }
