    type: CommandType.Both,
    plugins: [
        //This plugin prevents this command module from executing in other servers except myServer 
        fromCallback((ctx, args) => ctx.guildId == myServer)
    ],
    execute: ctx => {
        ctx.reply("I only respond in myServer!");
    }
})
